package com.swasthajiwan.swasthajiwan.controller.userAuthentication;import com.swasthajiwan.swasthajiwan.dto.LoginRequest;import com.swasthajiwan.swasthajiwan.dto.LoginResponse;import com.swasthajiwan.swasthajiwan.dto.UserRequest;import com.swasthajiwan.swasthajiwan.model.User;import com.swasthajiwan.swasthajiwan.services.userAuthentication.AuthenticationServices;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;@RestController@RequestMapping("/api/auth")public class AuthenticationController {    private final AuthenticationServices authService;    public AuthenticationController(AuthenticationServices authService){        this.authService=authService;    }    @PostMapping("/register")    public ResponseEntity<User>register(@RequestBody UserRequest request){       User user=authService.createUser(request);        return ResponseEntity.ok(user);    }    @PostMapping("/login")    public ResponseEntity<LoginResponse>login(@RequestBody LoginRequest request){        LoginResponse response=authService.login(request);        if(response.getUser()!=null){            response.getUser().setPassword(null);        }        return ResponseEntity.ok(response);    }}